<template>
      <div class="div-block-444">
                  <div class="div-block-447">
                    <div class="html-embed-7 w-embed">
                      <img
                        src="data:image/svg+xml;base64,"
                      />
                    </div>
                    <div class="text-block-17">{{labelRecomendados}}</div>
                  </div>
                  <div>
                    <ul role="list" class="list-10">
                      <li class="list-item-11" 
                      v-for="(item, index) in gruposRecomendados" :key="index">
                          <nuxt-link  
                          :to="{
                          name: 'g-slug',
                          params: { slug: item.slug },
                          }">{{item.titulo}}</nuxt-link>
                      </li>
                     
                    </ul>
                  </div>
                </div>
          
</template>


<script>

export default {
    name: "gruposRecomendados", 
    data() {
        return {
          gruposRecomendados: [], 
        labelRecomendados: 'Tus Grupos', 
        }
    },
    methods: {

          async   getGrupos(){

      await this.$axios
        .$get("/grupos/recomendados/?token="+this.$store.state.tokenUser)
        .then((response) => {
      //   console.log(response)
          if(response.status > 0){
            if(response.status == 2){
                this.labelRecomendados = 'Grupos Recomendados'
            }
            if(response.status == 1){
                this.labelRecomendados = 'Tus Grupos'
            }
              this.gruposRecomendados =  response.grupos
              
                //     this.$store.commit('setLoader', false);
              /// console.log("test loader", this.$store.state.loader)
          }
      
        });

      }
      
    },
  async  mounted() {
      this.getGrupos()

    },
}
</script>
